import React from 'react'
import axios from 'axios'
import Pokocard from './poko-card'
class App extends React.Component{
    state={
        value:''
    }
    searchValue=(event)=>{
        if(event.key==='Enter'){
            this.setState({value:event.target.value})
        }
    }
    render(){
        return(
            <>
            <div>
                <input className='form-control' style={{width:'47px'}} onKeyPress={(event)=>this.searchValue(event)}/>
            </div>
            <div  className='container'>
                <div className='row'>
                <div style={{borderRight:'2px solid black'}} className='col-md-4 offset-md-2 '>
                <h2>terferehr</h2>
                </div>
                <div className='col-md-4 offset-md-1 '>
                <button onClick={this.cli}>faegsrhe</button>
                <Pokocard/>
                </div>
                </div>
                
            </div>
            </>
        )
    }
}
export default App;
