import React from 'react'
import axios from 'axios'
import Loading from './loading'
class PokoList extends React.Component{
    state={
        data:'',
        isLoading:true,
        serachV:''
    }
    componentDidMount(){
        axios.get('https://pokeapi.co/api/v2/pokemon/?offset=20&limit=200')
        .then(res=>{
            console.log(res);
            this.setState({data:res.data.results,isLoading:false})
            setTimeout(() => {
                console.log(this.state.data);
            }, 1000);
        })
    }
    search=(event)=>{
        if(event.key==='Enter'){
            this.setState({serachV:event.target.value})
        }
    }
    show=()=>{
        let list=this.state.data.slice(0,20)
        return (<div>
                 <ul>
                 {
                  list.map(sig=>(<li>{sig.name}</li>))
                 }
                </ul>
        </div>)
    }
    render(){
        return(
            
            (
                this.state.isLoading ? <Loading/> : <div>
                    <p>Pokemon List</p>
                    <input className='form-control' onKeyPress={(event)=>this.search(event)}/>
                    {this.show()}
                </div>
            )
                
            
        
        )
    }
}
export default PokoList;
